contracts/plugins/interfaces/IPluginMana.content:
// SPDX-License-Identifier: GPL-2.0-or-later
pragma solidity ^0.8.0;

/// @title Plugin Manager Interface
/// @notice The interface defines the functions to manage plugins
interface IPluginManager {
    /// @notice Emitted when a new plugin is registered
    /// @param plugin The registered plugin
    event PluginRegistered(address indexed plugin);

    /// @notice Emitted when a plugin is approved
    /// @param account The account that approved the plugin
    /// @param plugin The approved plugin
    event PluginApproved(address indexed account, address indexed plugin);

    /// @notice Emitted when a plugin is revoked
    /// @param account The account that revoked the plugin
    /// @param plugin The revoked plugin
    event PluginRevoked(address indexed account, address indexed plugin);

    /// @notice Emitted when a new liquidator is registered
    /// @param liquidator The registered liquidator
    event LiquidatorRegistered(address indexed liquidator);

    /// @notice Plugin is already registered
    error PluginAlreadyRegistered(address plugin);
    /// @notice Plugin is not registered
    error PluginNotRegistered(address plugin);
    /// @notice Plugin is already approved
    error PluginAlreadyApproved(address sender, address plugin);
    /// @notice Plugin is not approved
    error PluginNotApproved(address sender, address plugin);
    /// @notice Liquidator is already registered
    error LiquidatorAlreadyRegistered(address liquidator);

    /// @notice Register a new plugin
    /// @dev The call will fail if the caller is not the governor or the plugin is already registered
    /// @param plugin The plugin to register
    function registerPlugin(address plugin) external;

    /// @notice Checks if a plugin is registered
    /// @param plugin The plugin to check
    /// @return True if the plugin is registered, false otherwise
    function registeredPlugins(address plugin) external view returns (bool);

    /// @notice Approve a plugin
    /// @dev The call will fail if the plugin is not registered or already approved
    /// @param plugin The plugin to approve
    function approvePlugin(address plugin) external;

    /// @notice Revoke approval for a plugin
    /// @dev The call will fail if the plugin is not approved
    /// @param plugin The plugin to revoke
    function revokePlugin(address plugin) external;

    /// @notice Checks if a plugin is approved for an account
    /// @param account The account to check
    /// @param plugin The plugin to check
    /// @return True if the plugin is approved for the account, false otherwise
    function isPluginApproved(address account, address plugin) external view returns (bool);

    /// @notice Register a new liquidator
    /// @dev The call will fail if the caller if not the governor or the liquidator is already registered
    /// @param liquidator The liquidator to register
    function registerLiquidator(address liquidator) external;

    /// @notice Checks if a liquidator is registered
    /// @param liquidator The liquidator to check
    /// @return True if the liquidator is registered, false otherwise
    function isRegisteredLiquidator(address liquidator) external view returns (bool);
}

contracts/plugins/interfaces/IPositionRo.content:
// SPDX-License-Identifier: GPL-2.0-or-later
pragma solidity ^0.8.0;

import "../../core/interfaces/IPool.sol";

interface IPositionRouter {
    /// @notice Emitted when min execution fee updated
    /// @param minExecutionFee New min execution fee after the update
    event MinExecutionFeeUpdated(uint256 minExecutionFee);

    /// @notice Emitted when position executor updated
    /// @param account Account to update
    /// @param active Whether active after the update
    event PositionExecutorUpdated(address indexed account, bool active);

    /// @notice Emitted when delay parameter updated
    /// @param minBlockDelayExecutor The new min block delay for executor to execute requests
    /// @param minTimeDelayPublic The new min time delay for public to execute requests
    /// @param maxTimeDelay The new max time delay until request expires
    event DelayValuesUpdated(uint32 minBlockDelayExecutor, uint32 minTimeDelayPublic, uint32 maxTimeDelay);

    /// @notice Emitted when open liquidity position request created
    /// @param account Owner of the request
    /// @param pool The address of the pool to open liquidity position
    /// @param margin Margin of the position
    /// @param liquidity Liquidity of the position
    /// @param executionFee Amount of fee for the executor to carry out the request
    /// @param index Index of the request
    event OpenLiquidityPositionCreated(
        address indexed account,
        IPool indexed pool,
        uint128 margin,
        uint256 liquidity,
        uint256 executionFee,
        uint128 indexed index
    );

    /// @notice Emitted when open liquidity position request cancelled
    /// @param index Index of the cancelled request
    /// @param executionFeeReceiver Receiver of the request execution fee
    event OpenLiquidityPositionCancelled(uint128 indexed index, address payable executionFeeReceiver);

    /// @notice Emitted when open liquidity position request executed
    /// @param index Index of the order to execute
    /// @param executionFeeReceiver Receiver of the order execution fee
    event OpenLiquidityPositionExecuted(uint128 indexed index, address payable executionFeeReceiver);

    /// @notice Emitted when close liquidity position request created
    /// @param account Owner of the request
    /// @param pool The address of the pool to close liquidity position
    /// @param positionID ID of the position
    /// @param receiver Address of the margin receiver
    /// @param executionFee  Amount of fee for the executor to carry out the request
    /// @param index Index of the request
    event CloseLiquidityPositionCreated(
        address indexed account,
        IPool indexed pool,
        uint96 positionID,
        address receiver,
        uint256 executionFee,
        uint128 indexed index
    );

    /// @notice Emitted when close liquidity position request cancelled
    /// @param index Index of cancelled request
    /// @param executionFeeReceiver Receiver of the request execution fee
    event CloseLiquidityPositionCancelled(uint128 indexed index, address payable executionFeeReceiver);

    /// @notice Emitted when close liquidity position request executed
    /// @param index Index of the executed request
    /// @param executionFeeReceiver Receiver of the request execution fee
    event CloseLiquidityPositionExecuted(uint128 indexed index, address payable executionFeeReceiver);

    /// @notice Emitted when adjust liquidity position margin request created
    /// @param account Owner of the request
    /// @param pool The address of the pool to adjust liquidity position margin
    /// @param positionID ID of the position
    /// @param marginDelta Delta of margin of the adjustment
    /// @param receiver Address of the margin receiver
    /// @param executionFee Amount of fee for the executor to carry out the request
    /// @param index Index of the request
    event AdjustLiquidityPositionMarginCreated(
        address indexed account,
        IPool indexed pool,
        uint96 positionID,
        int128 marginDelta,
        address receiver,
        uint256 executionFee,
        uint128 indexed index
    );

    /// @notice Emitted when adjust liquidity position margin request cancelled
    /// @param index Index of cancelled request
    /// @param executionFeeReceiver Receiver of the request execution fee
    event AdjustLiquidityPositionMarginCancelled(uint128 indexed index, address payable executionFeeReceiver);

    /// @notice Emitted when adjust liquidity position margin request executed
    /// @param index Index of executed request
    /// @param executionFeeReceiver Receiver of the request execution fee
    event AdjustLiquidityPositionMarginExecuted(uint128 indexed index, address payable executionFeeReceiver);

    /// @notice Emitted when increase risk buffer fund position request created
    /// @param account Owner of the request
    /// @param pool The address of the pool to increase risk buffer fund position
    /// @param liquidityDelta The increase in liquidity
    /// @param executionFee Amount of fee for the executor to carry out the request
    /// @param index Index of the request
    event IncreaseRiskBufferFundPositionCreated(
        address indexed account,
        IPool indexed pool,
        uint128 liquidityDelta,
        uint256 executionFee,
        uint128 indexed index
    );

    /// @notice Emitted when increase risk buffer fund position request cancelled
    /// @param index Index of the cancelled request
    /// @param executionFeeReceiver Receiver of the request execution fee
    event IncreaseRiskBufferFundPositionCancelled(uint128 indexed index, address payable executionFeeReceiver);

    /// @notice Emitted when increase risk buffer fund position request executed
    /// @param index Index of the executed request
    /// @param executionFeeReceiver Receiver of the request execution fee
    event IncreaseRiskBufferFundPositionExecuted(uint128 indexed index, address payable executionFeeReceiver);

    /// @notice Emitted when decrease risk buffer fund position request created
    /// @param account Owner of the request
    /// @param pool The address of the pool to decrease risk buffer fund position
    /// @param liquidityDelta The decrease in liquidity
    /// @param receiver Address of the margin receiver
    /// @param executionFee Amount of fee for the executor to carry out the request
    /// @param index Index of the request
    event DecreaseRiskBufferFundPositionCreated(
        address indexed account,
        IPool indexed pool,
        uint128 liquidityDelta,
        address receiver,
        uint256 executionFee,
        uint128 indexed index
    );

    /// @notice Emitted when decrease risk buffer fund position request cancelled
    /// @param index Index of the cancelled request
    /// @param executionFeeReceiver Receiver of the request execution fee
    event DecreaseRiskBufferFundPositionCancelled(uint128 indexed index, address payable executionFeeReceiver);

    /// @notice Emitted when decrease risk buffer fund position request executed
    /// @param index Index of the executed request
    /// @param executionFeeReceiver Receiver of the request execution fee
    event DecreaseRiskBufferFundPositionExecuted(uint128 indexed index, address payable executionFeeReceiver);

    /// @notice Emitted when open or increase an existing position size request created
    /// @param account Owner of the request
    /// @param pool The address of the pool to increase position
    /// @param side The side of the position (Long or Short)
    /// @param marginDelta The increase in position margin
    /// @param sizeDelta The increase in position size
    /// @param acceptableTradePriceX96 The worst trade price of the request
    /// @param executionFee Amount of fee for the executor to carry out the request
    /// @param index Index of the request
    event IncreasePositionCreated(
        address indexed account,
        IPool indexed pool,
        Side side,
        uint128 marginDelta,
        uint128 sizeDelta,
        uint160 acceptableTradePriceX96,
        uint256 executionFee,
        uint128 indexed index
    );

    /// @notice Emitted when increase position request cancelled
    /// @param index Index of the cancelled request
    /// @param executionFeeReceiver Receiver of the cancelled request execution fee
    event IncreasePositionCancelled(uint128 indexed index, address payable executionFeeReceiver);

    /// @notice Emitted when increase position request executed
    /// @param index Index of the executed request
    /// @param executionFeeReceiver Receiver of the executed request execution fee
    event IncreasePositionExecuted(uint128 indexed index, address payable executionFeeReceiver);

    /// @notice Emitted when close or decrease existing position size request created
    /// @param account Owner of the request
    /// @param pool The address of the pool to decrease position
    /// @param side The side of the position (Long or Short)
    /// @param marginDelta The decrease in position margin
    /// @param sizeDelta The decrease in position size
    /// @param acceptableTradePriceX96 The worst trade price of the request
    /// @param receiver Address of the margin receiver
    /// @param executionFee Amount of fee for the executor to carry out the order
    /// @param index Index of the request
    event DecreasePositionCreated(
        address indexed account,
        IPool indexed pool,
        Side side,
        uint128 marginDelta,
        uint128 sizeDelta,
        uint160 acceptableTradePriceX96,
        address receiver,
        uint256 executionFee,
        uint128 indexed index
    );

    /// @notice Emitted when decrease position request cancelled
    /// @param index Index of the cancelled decrease position request
    /// @param executionFeeReceiver Receiver of the request execution fee
    event DecreasePositionCancelled(uint128 indexed index, address payable executionFeeReceiver);

    /// @notice Emitted when decrease position request executed
    /// @param index Index of the executed decrease position request
    /// @param executionFeeReceiver Receiver of the request execution fee
    event DecreasePositionExecuted(uint128 indexed index, address payable executionFeeReceiver);

    /// @notice Execution fee is insufficient
    /// @param available The available execution fee amount
    /// @param required The required minimum execution fee amount
    error InsufficientExecutionFee(uint256 available, uint256 required);

    /// @notice Request expired
    /// @param expiredAt When the request is expired
    error Expired(uint256 expiredAt);

    /// @notice Too early to execute request
    /// @param earliest The earliest time to execute the request
    error TooEarly(uint256 earliest);

    /// @notice Trade price exceeds limit
    error InvalidTradePrice(uint160 tradePriceX96, uint160 acceptableTradePriceX96);

    struct OpenLiquidityPositionRequest {
        address account;
        uint96 blockNumber;
        IPool pool;
        uint64 blockTime;
        uint128 margin;
        uint128 liquidity;
        uint256 executionFee;
    }

    struct CloseLiquidityPositionRequest {
        address account;
        uint96 positionID;
        IPool pool;
        uint96 blockNumber;
        uint256 executionFee;
        address receiver;
        uint64 blockTime;
    }

    struct AdjustLiquidityPositionMarginRequest {
        address account;
        uint96 positionID;
        IPool pool;
        uint96 blockNumber;
        int128 marginDelta;
        uint64 blockTime;
        address receiver;
        uint256 executionFee;
    }

    struct IncreaseRiskBufferFundPositionRequest {
        address account;
        uint96 blockNumber;
        IPool pool;
        uint64 blockTime;
        uint128 liquidityDelta;
        uint256 executionFee;
    }

    struct DecreaseRiskBufferFundPositionRequest {
        address account;
        uint96 blockNumber;
        IPool pool;
        uint64 blockTime;
        uint128 liquidityDelta;
        address receiver;
        uint256 executionFee;
    }

    struct IncreasePositionRequest {
        address account;
        uint96 blockNumber;
        IPool pool;
        uint128 marginDelta;
        uint128 sizeDelta;
        uint160 acceptableTradePriceX96;
        uint64 blockTime;
        Side side;
        uint256 executionFee;
    }

    struct DecreasePositionRequest {
        address account;
        uint96 blockNumber;
        IPool pool;
        uint128 marginDelta;
        uint128 sizeDelta;
        uint160 acceptableTradePriceX96;
        uint64 blockTime;
        Side side;
        address receiver;
        uint256 executionFee;
    }

    /// @notice Update position executor
    /// @param account Account to update
    /// @param active Updated status
    function updatePositionExecutor(address account, bool active) external;

    /// @notice Update delay parameters
    /// @param minBlockDelayExecutor New min block delay for executor to execute requests
    /// @param minTimeDelayPublic New min time delay for public to execute requests
    /// @param maxTimeDelay New max time delay until request expires
    function updateDelayValues(uint32 minBlockDelayExecutor, uint32 minTimeDelayPublic, uint32 maxTimeDelay) external;

    /// @notice Update minimum execution fee
    /// @param minExecutionFee New min execution fee
    function updateMinExecutionFee(uint256 minExecutionFee) external;

    /// @notice Update the gas limit for executing requests
    /// @param executionGasLimit New execution gas limit
    function updateExecutionGasLimit(uint160 executionGasLimit) external;

    /// @notice Create open liquidity position request
    /// @param pool The address of the pool to open liquidity position
    /// @param margin Margin of the position
    /// @param liquidity Liquidity of the position
    /// @return index Index of the request
    function createOpenLiquidityPosition(
        IPool pool,
        uint128 margin,
        uint128 liquidity
    ) external payable returns (uint128 index);

    /// @notice Cancel open liquidity position request
    /// @param index Index of the request to cancel
    /// @param executionFeeReceiver Receiver of request execution fee
    /// @return cancelled True if the cancellation succeeds or request not exists
    function cancelOpenLiquidityPosition(
        uint128 index,
        address payable executionFeeReceiver
    ) external returns (bool cancelled);

    /// @notice Execute open liquidity position request
    /// @param index Index of request to execute
    /// @param executionFeeReceiver Receiver of the request execution fee
    /// @return executed True if the execution succeeds or request not exists
    function executeOpenLiquidityPosition(
        uint128 index,
        address payable executionFeeReceiver
    ) external returns (bool executed);

    /// @notice Execute multiple liquidity position requests
    /// @param endIndex The maximum request index to execute, excluded
    /// @param executionFeeReceiver Receiver of the request execution fees
    function executeOpenLiquidityPositions(uint128 endIndex, address payable executionFeeReceiver) external;

    /// @notice Create close liquidity position request
    /// @param pool The address of the pool to close liquidity position
    /// @param positionID ID of the position
    /// @param receiver Address of the margin receiver
    /// @return index The request index
    function createCloseLiquidityPosition(
        IPool pool,
        uint96 positionID,
        address receiver
    ) external payable returns (uint128 index);

    /// @notice Cancel close liquidity position request
    /// @param index Index of the request to cancel
    /// @param executionFeeReceiver Receiver of the request execution fee
    /// @return cancelled True if the cancellation succeeds or request not exists
    function cancelCloseLiquidityPosition(
        uint128 index,
        address payable executionFeeReceiver
    ) external returns (bool cancelled);

    /// @notice Execute close liquidity position request
    /// @param index Index of the request to execute
    /// @param executionFeeReceiver Receiver of the request execution fee
    /// @return executed True if the execution succeeds or request not exists
    function executeCloseLiquidityPosition(
        uint128 index,
        address payable executionFeeReceiver
    ) external returns (bool executed);

    /// @notice Execute multiple close liquidity position requests
    /// @param endIndex The maximum request index to execute, excluded
    /// @param executionFeeReceiver Receiver of the request execution fee
    function executeCloseLiquidityPositions(uint128 endIndex, address payable executionFeeReceiver) external;

    /// @notice Create adjust liquidity position margin request
    /// @param pool The address of the pool to adjust liquidity position margin
    /// @param positionID ID of the position
    /// @param marginDelta Delta of margin of the adjustment
    /// @param receiver Address of the margin receiver
    /// @return index Index of the request
    function createAdjustLiquidityPositionMargin(
        IPool pool,
        uint96 positionID,
        int128 marginDelta,
        address receiver
    ) external payable returns (uint128 index);

    /// @notice Cancel adjust liquidity position margin request
    /// @param index Index of the request to cancel
    /// @param executionFeeReceiver Receiver of the request execution fee
    /// @return cancelled True if the cancellation succeeds or request not exists
    function cancelAdjustLiquidityPositionMargin(
        uint128 index,
        address payable executionFeeReceiver
    ) external returns (bool cancelled);

    /// @notice Execute adjust liquidity position margin request
    /// @param index Index of the request to execute
    /// @param executionFeeReceiver Receiver of the request execution fee
    /// @return executed True if the execution succeeds or request not exists
    function executeAdjustLiquidityPositionMargin(
        uint128 index,
        address payable executionFeeReceiver
    ) external returns (bool executed);

    /// @notice Execute multiple adjust liquidity position margin requests
    /// @param endIndex The maximum request index to execute, excluded
    /// @param executionFeeReceiver Receiver of the request execution fee
    function executeAdjustLiquidityPositionMargins(uint128 endIndex, address payable executionFeeReceiver) external;

    /// @notice Create increase risk buffer fund position request
    /// @param pool The address of the pool to increase risk buffer fund position
    /// @param liquidityDelta The increase in liquidity
    /// @return index Index of the request
    function createIncreaseRiskBufferFundPosition(
        IPool pool,
        uint128 liquidityDelta
    ) external payable returns (uint128 index);

    /// @notice Cancel increase risk buffer fund position request
    /// @param index Index of the request to cancel
    /// @param executionFeeReceiver Receiver of the request execution fee
    /// @return cancelled True if the cancellation succeeds or request not exists
    function cancelIncreaseRiskBufferFundPosition(
        uint128 index,
        address payable executionFeeReceiver
    ) external returns (bool cancelled);

    /// @notice Execute increase risk buffer fund position request
    /// @param index Index of the request to execute
    /// @param executionFeeReceiver Receiver of the request execution fee
    /// @return executed True if the execution succeeds or request not exists
    function executeIncreaseRiskBufferFundPosition(
        uint128 index,
        address payable executionFeeReceiver
    ) external returns (bool executed);

    /// @notice Execute multiple increase risk buffer fund position requests
    /// @param endIndex The maximum request index to execute, excluded
    /// @param executionFeeReceiver Receiver of the request execution fee
    function executeIncreaseRiskBufferFundPositions(uint128 endIndex, address payable executionFeeReceiver) external;

    /// @notice Create decrease risk buffer fund position request
    /// @param pool The address of the pool to decrease risk buffer fund position
    /// @param liquidityDelta The decrease in liquidity
    /// @param receiver Address of the margin receiver
    /// @return index Index of the request
    function createDecreaseRiskBufferFundPosition(
        IPool pool,
        uint128 liquidityDelta,
        address receiver
    ) external payable returns (uint128 index);

    /// @notice Cancel decrease risk buffer fund position request
    /// @param index Index of the request to cancel
    /// @param executionFeeReceiver Receiver of the request execution fee
    /// @return cancelled True if the cancellation succeeds or request not exists
    function cancelDecreaseRiskBufferFundPosition(
        uint128 index,
        address payable executionFeeReceiver
    ) external returns (bool cancelled);

    /// @notice Execute decrease risk buffer fund position request
    /// @param index Index of the request to execute
    /// @param executionFeeReceiver Receiver of the request execution fee
    /// @return executed True if the execution succeeds or request not exists
    function executeDecreaseRiskBufferFundPosition(
        uint128 index,
        address payable executionFeeReceiver
    ) external returns (bool executed);

    /// @notice Execute multiple decrease risk buffer fund position requests
    /// @param endIndex The maximum request index to execute, excluded
    /// @param executionFeeReceiver Receiver of the request execution fee
    function executeDecreaseRiskBufferFundPositions(uint128 endIndex, address payable executionFeeReceiver) external;

    /// @notice Create open or increase the size of existing position request
    /// @param pool The address of the pool to increase position
    /// @param side The side of the position (Long or Short)
    /// @param marginDelta The increase in position margin
    /// @param sizeDelta The increase in position size
    /// @param acceptableTradePriceX96 The worst trade price of the request, as a Q64.96
    /// @return index Index of the request
    function createIncreasePosition(
        IPool pool,
        Side side,
        uint128 marginDelta,
        uint128 sizeDelta,
        uint160 acceptableTradePriceX96
    ) external payable returns (uint128 index);

    /// @notice Cancel increase position request
    /// @param index Index of the request to cancel
    /// @param executionFeeReceiver Receiver of the request execution fee
    /// @return cancelled True if the cancellation succeeds or request not exists
    function cancelIncreasePosition(
        uint128 index,
        address payable executionFeeReceiver
    ) external returns (bool cancelled);

    /// @notice Execute increase position request
    /// @param index Index of the request to execute
    /// @param executionFeeReceiver Receiver of the request execution fee
    /// @return executed True if the execution succeeds or request not exists
    function executeIncreasePosition(
        uint128 index,
        address payable executionFeeReceiver
    ) external returns (bool executed);

    /// @notice Execute multiple increase position requests
    /// @param endIndex The maximum request index to execute, excluded
    /// @param executionFeeReceiver Receiver of the request execution fee
    function executeIncreasePositions(uint128 endIndex, address payable executionFeeReceiver) external;

    /// @notice Create decrease position request
    /// @param pool The address of the pool to decrease position
    /// @param side The side of the position (Long or Short)
    /// @param marginDelta The decrease in position margin
    /// @param sizeDelta The decrease in position size
    /// @param acceptableTradePriceX96 The worst trade price of the request, as a Q64.96
    /// @param receiver Margin recipient address
    /// @return index The request index
    function createDecreasePosition(
        IPool pool,
        Side side,
        uint128 marginDelta,
        uint128 sizeDelta,
        uint160 acceptableTradePriceX96,
        address receiver
    ) external payable returns (uint128 index);

    /// @notice Cancel decrease position request
    /// @param index Index of the request to cancel
    /// @param executionFeeReceiver Receiver of the request execution fee
    /// @return cancelled True if the cancellation succeeds or request not exists
    function cancelDecreasePosition(
        uint128 index,
        address payable executionFeeReceiver
    ) external returns (bool cancelled);

    /// @notice Execute decrease position request
    /// @param index Index of the request to execute
    /// @param executionFeeReceiver Receiver of the request execution fee
    /// @return executed True if the execution succeeds or request not exists
    function executeDecreasePosition(
        uint128 index,
        address payable executionFeeReceiver
    ) external returns (bool executed);

    /// @notice Execute multiple decrease position requests
    /// @param endIndex The maximum request index to execute, excluded
    /// @param executionFeeReceiver Receiver of the request execution fee
    function executeDecreasePositions(uint128 endIndex, address payable executionFeeReceiver) external;
}

contracts/plugins/PluginManager.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.0;

import "../governance/Governable.sol";
import "./interfaces/IPluginManager.sol";

abstract contract PluginManager is IPluginManager, Governable {
    /// @inheritdoc IPluginManager
    mapping(address => bool) public override registeredPlugins;
    mapping(address => bool) private registeredLiquidators;
    mapping(address => mapping(address => bool)) private pluginApprovals;

    /// @inheritdoc IPluginManager
    function registerPlugin(address _plugin) external override onlyGov {
        if (registeredPlugins[_plugin]) revert PluginAlreadyRegistered(_plugin);

        registeredPlugins[_plugin] = true;

        emit PluginRegistered(_plugin);
    }

    /// @inheritdoc IPluginManager
    function approvePlugin(address _plugin) external override {
        if (pluginApprovals[msg.sender][_plugin]) revert PluginAlreadyApproved(msg.sender, _plugin);

        if (!registeredPlugins[_plugin]) revert PluginNotRegistered(_plugin);

        pluginApprovals[msg.sender][_plugin] = true;
        emit PluginApproved(msg.sender, _plugin);
    }

    /// @inheritdoc IPluginManager
    function revokePlugin(address _plugin) external {
        if (!pluginApprovals[msg.sender][_plugin]) revert PluginNotApproved(msg.sender, _plugin);

        delete pluginApprovals[msg.sender][_plugin];
        emit PluginRevoked(msg.sender, _plugin);
    }

    /// @inheritdoc IPluginManager
    function isPluginApproved(address _account, address _plugin) public view override returns (bool) {
        return pluginApprovals[_account][_plugin];
    }

    /// @inheritdoc IPluginManager
    function registerLiquidator(address _liquidator) external override onlyGov {
        if (registeredLiquidators[_liquidator]) revert LiquidatorAlreadyRegistered(_liquidator);

        registeredLiquidators[_liquidator] = true;

        emit LiquidatorRegistered(_liquidator);
    }

    /// @inheritdoc IPluginManager
    function isRegisteredLiquidator(address _liquidator) public view override returns (bool) {
        return registeredLiquidators[_liquidator];
    }
}

contracts/plugins/PositionRouter.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity =0.8.21;

import "./Router.sol";
import "../libraries/SafeCast.sol";
import "../libraries/ReentrancyGuard.sol";
import {M as Math} from "../libraries/Math.sol";
import "./interfaces/IPositionRouter.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";

contract PositionRouter is IPositionRouter, Governable, ReentrancyGuard {
    using SafeCast for uint256;
    using SafeMath for uint256;
    using SafeERC20 for IERC20;
    using Address for address payable;

    IERC20 public immutable usd;
    Router public immutable router;

    uint256 public minExecutionFee;

    // pack into a single slot to save gas
    uint32 public minBlockDelayExecutor;
    uint32 public minTimeDelayPublic = 3 minutes;
    uint32 public maxTimeDelay = 30 minutes;
    uint160 public executionGasLimit = 1_000_000 wei;

    mapping(address => bool) public positionExecutors;

    // LP stats
    mapping(uint128 => OpenLiquidityPositionRequest) public openLiquidityPositionRequests;
    uint128 public openLiquidityPositionIndex;
    uint128 public openLiquidityPositionIndexNext;

    mapping(uint128 => CloseLiquidityPositionRequest) public closeLiquidityPositionRequests;
    uint128 public closeLiquidityPositionIndex;
    uint128 public closeLiquidityPositionIndexNext;

    mapping(uint128 => AdjustLiquidityPositionMarginRequest) public adjustLiquidityPositionMarginRequests;
    uint128 public adjustLiquidityPositionMarginIndex;
    uint128 public adjustLiquidityPositionMarginIndexNext;

    mapping(uint128 => IncreaseRiskBufferFundPositionRequest) public increaseRiskBufferFundPositionRequests;
    uint128 public increaseRiskBufferFundPositionIndex;
    uint128 public increaseRiskBufferFundPositionIndexNext;

    mapping(uint128 => DecreaseRiskBufferFundPositionRequest) public decreaseRiskBufferFundPositionRequests;
    uint128 public decreaseRiskBufferFundPositionIndex;
    uint128 public decreaseRiskBufferFundPositionIndexNext;

    // Trader stats
    mapping(uint128 => IncreasePositionRequest) public increasePositionRequests;
    uint128 public increasePositionIndex;
    uint128 public increasePositionIndexNext;

    mapping(uint128 => DecreasePositionRequest) public decreasePositionRequests;
    uint128 public decreasePositionIndex;
    uint128 public decreasePositionIndexNext;

    modifier onlyPositionExecutor() {
        if (!positionExecutors[msg.sender]) revert Forbidden();
        _;
    }

    constructor(IERC20 _usd, Router _router, uint256 _minExecutionFee) {
        usd = _usd;
        router = _router;
        minExecutionFee = _minExecutionFee;
        emit MinExecutionFeeUpdated(_minExecutionFee);
    }

    /// @inheritdoc IPositionRouter
    function updatePositionExecutor(address _account, bool _active) external override onlyGov {
        positionExecutors[_account] = _active;
        emit PositionExecutorUpdated(_account, _active);
    }

    /// @inheritdoc IPositionRouter
    function updateDelayValues(
        uint32 _minBlockDelayExecutor,
        uint32 _minTimeDelayPublic,
        uint32 _maxTimeDelay
    ) external override onlyGov {
        minBlockDelayExecutor = _minBlockDelayExecutor;
        minTimeDelayPublic = _minTimeDelayPublic;
        maxTimeDelay = _maxTimeDelay;
        emit DelayValuesUpdated(_minBlockDelayExecutor, _minTimeDelayPublic, _maxTimeDelay);
    }

    /// @inheritdoc IPositionRouter
    function updateMinExecutionFee(uint256 _minExecutionFee) external override onlyGov {
        minExecutionFee = _minExecutionFee;
        emit MinExecutionFeeUpdated(_minExecutionFee);
    }

    /// @inheritdoc IPositionRouter
    function updateExecutionGasLimit(uint160 _executionGasLimit) external override onlyGov {
        executionGasLimit = _executionGasLimit;
    }

    /// @inheritdoc IPositionRouter
    function createOpenLiquidityPosition(
        IPool _pool,
        uint128 _margin,
        uint128 _liquidity
    ) external payable override nonReentrant returns (uint128 index) {
        if (msg.value < minExecutionFee) revert InsufficientExecutionFee(msg.value, minExecutionFee);

        router.pluginTransfer(usd, msg.sender, address(this), _margin);

        index = openLiquidityPositionIndexNext++;
        openLiquidityPositionRequests[index] = OpenLiquidityPositionRequest({
            account: msg.sender,
            pool: _pool,
            margin: _margin,
            liquidity: _liquidity,
            executionFee: msg.value,
            blockNumber: block.number.toUint96(),
            blockTime: block.timestamp.toUint64()
        });

        emit OpenLiquidityPositionCreated(msg.sender, _pool, _margin, _liquidity, msg.value, index);
    }

    /// @inheritdoc IPositionRouter
    function cancelOpenLiquidityPosition(
        uint128 _index,
        address payable _executionFeeReceiver
    ) external override nonReentrant returns (bool) {
        OpenLiquidityPositionRequest memory request = openLiquidityPositionRequests[_index];
        if (request.account == address(0)) return true;

        bool shouldCancel = _shouldCancel(request.blockNumber, request.blockTime, request.account);
        if (!shouldCancel) return false;

        usd.safeTransfer(request.account, request.margin);

        _transferOutETH(request.executionFee, _executionFeeReceiver);

        delete openLiquidityPositionRequests[_index];

        emit OpenLiquidityPositionCancelled(_index, _executionFeeReceiver);

        return true;
    }

    /// @inheritdoc IPositionRouter
    function executeOpenLiquidityPosition(
        uint128 _index,
        address payable _executionFeeReceiver
    ) external override nonReentrant returns (bool) {
        OpenLiquidityPositionRequest memory request = openLiquidityPositionRequests[_index];
        if (request.account == address(0)) return true;

        bool shouldExecute = _shouldExecute(request.blockNumber, request.blockTime, request.account);
        if (!shouldExecute) return false;

        usd.safeTransfer(address(request.pool), request.margin);

        // Note that the gas specified here is just an upper limit,
        // when the gas left is lower than this value, code can still be executed
        router.pluginOpenLiquidityPosition{gas: executionGasLimit}(
            request.pool,
            request.account,
            request.margin,
            request.liquidity
        );

        _transferOutETH(request.executionFee, _executionFeeReceiver);

        delete openLiquidityPositionRequests[_index];

        emit OpenLiquidityPositionExecuted(_index, _executionFeeReceiver);
        return true;
    }

    /// @inheritdoc IPositionRouter
    function executeOpenLiquidityPositions(
        uint128 _endIndex,
        address payable _executionFeeReceiver
    ) external override onlyPositionExecutor {
        uint128 index = openLiquidityPositionIndex;
        _endIndex = uint128(Math.min(_endIndex, openLiquidityPositionIndexNext));

        while (index < _endIndex) {
            try this.executeOpenLiquidityPosition(index, _executionFeeReceiver) returns (bool _executed) {
                if (!_executed) break;
            } catch {
                try this.cancelOpenLiquidityPosition(index, _executionFeeReceiver) returns (bool _cancelled) {
                    if (!_cancelled) break;
                } catch {}
            }
            // prettier-ignore
            unchecked { ++index; }
        }

        openLiquidityPositionIndex = index;
    }

    /// @inheritdoc IPositionRouter
    function createCloseLiquidityPosition(
        IPool _pool,
        uint96 _positionID,
        address _receiver
    ) external payable override nonReentrant returns (uint128 index) {
        if (msg.value < minExecutionFee) revert InsufficientExecutionFee(msg.value, minExecutionFee);

        address owner = _pool.liquidityPositionAccount(_positionID);
        if (owner != msg.sender) revert Forbidden();

        index = closeLiquidityPositionIndexNext++;
        closeLiquidityPositionRequests[index] = CloseLiquidityPositionRequest({
            account: msg.sender,
            pool: _pool,
            positionID: _positionID,
            receiver: _receiver,
            executionFee: msg.value,
            blockNumber: block.number.toUint96(),
            blockTime: block.timestamp.toUint64()
        });

        emit CloseLiquidityPositionCreated(msg.sender, _pool, _positionID, _receiver, msg.value, index);
    }

    /// @inheritdoc IPositionRouter
    function cancelCloseLiquidityPosition(
        uint128 _index,
        address payable _executionFeeReceiver
    ) external override nonReentrant returns (bool) {
        CloseLiquidityPositionRequest memory request = closeLiquidityPositionRequests[_index];
        if (request.account == address(0)) return true;

        bool shouldCancel = _shouldCancel(request.blockNumber, request.blockTime, request.account);
        if (!shouldCancel) return false;

        _transferOutETH(request.executionFee, _executionFeeReceiver);

        delete closeLiquidityPositionRequests[_index];

        emit CloseLiquidityPositionCancelled(_index, _executionFeeReceiver);

        return true;
    }

    /// @inheritdoc IPositionRouter
    function executeCloseLiquidityPosition(
        uint128 _index,
        address payable _executionFeeReceiver
    ) external override nonReentrant returns (bool) {
        CloseLiquidityPositionRequest memory request = closeLiquidityPositionRequests[_index];
        if (request.account == address(0)) return true;

        bool shouldExecute = _shouldExecute(request.blockNumber, request.blockTime, request.account);
        if (!shouldExecute) return false;

        router.pluginCloseLiquidityPosition{gas: executionGasLimit}(request.pool, request.positionID, request.receiver);

        _transferOutETH(request.executionFee, _executionFeeReceiver);

        delete closeLiquidityPositionRequests[_index];

        emit CloseLiquidityPositionExecuted(_index, _executionFeeReceiver);
        return true;
    }

    /// @inheritdoc IPositionRouter
    function executeCloseLiquidityPositions(
        uint128 _endIndex,
        address payable _executionFeeReceiver
    ) external override onlyPositionExecutor {
        uint128 index = closeLiquidityPositionIndex;
        _endIndex = uint128(Math.min(_endIndex, closeLiquidityPositionIndexNext));

        while (index < _endIndex) {
            try this.executeCloseLiquidityPosition(index, _executionFeeReceiver) returns (bool _executed) {
                if (!_executed) break;
            } catch {
                try this.cancelCloseLiquidityPosition(index, _executionFeeReceiver) returns (bool _cancelled) {
                    if (!_cancelled) break;
                } catch {}
            }
            // prettier-ignore
            unchecked { ++index; }
        }

        closeLiquidityPositionIndex = index;
    }

    /// @inheritdoc IPositionRouter
    function createAdjustLiquidityPositionMargin(
        IPool _pool,
        uint96 _positionID,
        int128 _marginDelta,
        address _receiver
    ) external payable override nonReentrant returns (uint128 index) {
        if (msg.value < minExecutionFee) revert InsufficientExecutionFee(msg.value, minExecutionFee);

        address owner = _pool.liquidityPositionAccount(_positionID);
        if (owner != msg.sender) revert Forbidden();

        if (_marginDelta > 0) router.pluginTransfer(usd, msg.sender, address(this), uint128(_marginDelta));

        index = adjustLiquidityPositionMarginIndexNext++;
        adjustLiquidityPositionMarginRequests[index] = AdjustLiquidityPositionMarginRequest({
            account: msg.sender,
            pool: _pool,
            positionID: _positionID,
            marginDelta: _marginDelta,
            receiver: _receiver,
            executionFee: msg.value,
            blockNumber: block.number.toUint96(),
            blockTime: block.timestamp.toUint64()
        });

        emit AdjustLiquidityPositionMarginCreated(
            msg.sender,
            _pool,
            _positionID,
            _marginDelta,
            _receiver,
            msg.value,
            index
        );
    }

    /// @inheritdoc IPositionRouter
    function cancelAdjustLiquidityPositionMargin(
        uint128 _index,
        address payable _executionFeeReceiver
    ) external override nonReentrant returns (bool) {
        AdjustLiquidityPositionMarginRequest memory request = adjustLiquidityPositionMarginRequests[_index];
        if (request.account == address(0)) return true;

        bool shouldCancel = _shouldCancel(request.blockNumber, request.blockTime, request.account);
        if (!shouldCancel) return false;

        if (request.marginDelta > 0) usd.safeTransfer(request.account, uint128(request.marginDelta));

        _transferOutETH(request.executionFee, _executionFeeReceiver);

        delete adjustLiquidityPositionMarginRequests[_index];

        emit AdjustLiquidityPositionMarginCancelled(_index, _executionFeeReceiver);

        return true;
    }

    /// @inheritdoc IPositionRouter
    function executeAdjustLiquidityPositionMargin(
        uint128 _index,
        address payable _executionFeeReceiver
    ) external override nonReentrant returns (bool) {
        AdjustLiquidityPositionMarginRequest memory request = adjustLiquidityPositionMarginRequests[_index];
        if (request.account == address(0)) return true;

        bool shouldExecute = _shouldExecute(request.blockNumber, request.blockTime, request.account);
        if (!shouldExecute) return false;

        if (request.marginDelta > 0) usd.safeTransfer(address(request.pool), uint128(request.marginDelta));

        router.pluginAdjustLiquidityPositionMargin{gas: executionGasLimit}(
            request.pool,
            request.positionID,
            request.marginDelta,
            request.receiver
        );

        _transferOutETH(request.executionFee, _executionFeeReceiver);

        delete adjustLiquidityPositionMarginRequests[_index];

        emit AdjustLiquidityPositionMarginExecuted(_index, _executionFeeReceiver);
        return true;
    }

    /// @inheritdoc IPositionRouter
    function executeAdjustLiquidityPositionMargins(
        uint128 _endIndex,
        address payable _executionFeeReceiver
    ) external override onlyPositionExecutor {
        uint128 index = adjustLiquidityPositionMarginIndex;
        _endIndex = uint128(Math.min(_endIndex, adjustLiquidityPositionMarginIndexNext));

        while (index < _endIndex) {
            try this.executeAdjustLiquidityPositionMargin(index, _executionFeeReceiver) returns (bool _executed) {
                if (!_executed) break;
            } catch {
                try this.cancelAdjustLiquidityPositionMargin(index, _executionFeeReceiver) returns (bool _cancelled) {
                    if (!_cancelled) break;
                } catch {}
            }
            // prettier-ignore
            unchecked { ++index; }
        }

        adjustLiquidityPositionMarginIndex = index;
    }

    /// @inheritdoc IPositionRouter
    function createIncreaseRiskBufferFundPosition(
        IPool _pool,
        uint128 _liquidityDelta
    ) external payable override nonReentrant returns (uint128 index) {
        if (msg.value < minExecutionFee) revert InsufficientExecutionFee(msg.value, minExecutionFee);

        router.pluginTransfer(usd, msg.sender, address(this), _liquidityDelta);

        index = increaseRiskBufferFundPositionIndexNext++;
        increaseRiskBufferFundPositionRequests[index] = IncreaseRiskBufferFundPositionRequest({
            account: msg.sender,
            pool: _pool,
            liquidityDelta: _liquidityDelta,
            executionFee: msg.value,
            blockNumber: block.number.toUint96(),
            blockTime: block.timestamp.toUint64()
        });

        emit IncreaseRiskBufferFundPositionCreated(msg.sender, _pool, _liquidityDelta, msg.value, index);
    }

    /// @inheritdoc IPositionRouter
    function cancelIncreaseRiskBufferFundPosition(
        uint128 _index,
        address payable _executionFeeReceiver
    ) external override nonReentrant returns (bool) {
        IncreaseRiskBufferFundPositionRequest memory request = increaseRiskBufferFundPositionRequests[_index];
        if (request.account == address(0)) return true;

        bool shouldCancel = _shouldCancel(request.blockNumber, request.blockTime, request.account);
        if (!shouldCancel) return false;

        usd.safeTransfer(request.account, request.liquidityDelta);

        _transferOutETH(request.executionFee, _executionFeeReceiver);

        delete increaseRiskBufferFundPositionRequests[_index];

        emit IncreaseRiskBufferFundPositionCancelled(_index, _executionFeeReceiver);

        return true;
    }

    /// @inheritdoc IPositionRouter
    function executeIncreaseRiskBufferFundPosition(
        uint128 _index,
        address payable _executionFeeReceiver
    ) external override nonReentrant returns (bool) {
        IncreaseRiskBufferFundPositionRequest memory request = increaseRiskBufferFundPositionRequests[_index];
        if (request.account == address(0)) return true;

        bool shouldExecute = _shouldExecute(request.blockNumber, request.blockTime, request.account);
        if (!shouldExecute) return false;

        usd.safeTransfer(address(request.pool), request.liquidityDelta);

        router.pluginIncreaseRiskBufferFundPosition{gas: executionGasLimit}(
            request.pool,
            request.account,
            request.liquidityDelta
        );

        _transferOutETH(request.executionFee, _executionFeeReceiver);

        delete increaseRiskBufferFundPositionRequests[_index];

        emit IncreaseRiskBufferFundPositionExecuted(_index, _executionFeeReceiver);
        return true;
    }

    /// @inheritdoc IPositionRouter
    function executeIncreaseRiskBufferFundPositions(
        uint128 _endIndex,
        address payable _executionFeeReceiver
    ) external override onlyPositionExecutor {
        uint128 index = increaseRiskBufferFundPositionIndex;
        _endIndex = uint128(Math.min(_endIndex, increaseRiskBufferFundPositionIndexNext));

        while (index < _endIndex) {
            try this.executeIncreaseRiskBufferFundPosition(index, _executionFeeReceiver) returns (bool _executed) {
                if (!_executed) break;
            } catch {
                try this.cancelIncreaseRiskBufferFundPosition(index, _executionFeeReceiver) returns (bool _cancelled) {
                    if (!_cancelled) break;
                } catch {}
            }
            // prettier-ignore
            unchecked { ++index; }
        }

        increaseRiskBufferFundPositionIndex = index;
    }

    /// @inheritdoc IPositionRouter
    function createDecreaseRiskBufferFundPosition(
        IPool _pool,
        uint128 _liquidityDelta,
        address _receiver
    ) external payable override nonReentrant returns (uint128 index) {
        if (msg.value < minExecutionFee) revert InsufficientExecutionFee(msg.value, minExecutionFee);

        index = decreaseRiskBufferFundPositionIndexNext++;
        decreaseRiskBufferFundPositionRequests[index] = DecreaseRiskBufferFundPositionRequest({
            account: msg.sender,
            pool: _pool,
            liquidityDelta: _liquidityDelta,
            receiver: _receiver,
            executionFee: msg.value,
            blockNumber: block.number.toUint96(),
            blockTime: block.timestamp.toUint64()
        });

        emit DecreaseRiskBufferFundPositionCreated(msg.sender, _pool, _liquidityDelta, _receiver, msg.value, index);
    }

    /// @inheritdoc IPositionRouter
    function cancelDecreaseRiskBufferFundPosition(
        uint128 _index,
        address payable _executionFeeReceiver
    ) external override nonReentrant returns (bool) {
        DecreaseRiskBufferFundPositionRequest memory request = decreaseRiskBufferFundPositionRequests[_index];
        if (request.account == address(0)) return true;

        bool shouldCancel = _shouldCancel(request.blockNumber, request.blockTime, request.account);
        if (!shouldCancel) return false;

        _transferOutETH(request.executionFee, _executionFeeReceiver);

        delete decreaseRiskBufferFundPositionRequests[_index];

        emit DecreaseRiskBufferFundPositionCancelled(_index, _executionFeeReceiver);

        return true;
    }

    /// @inheritdoc IPositionRouter
    function executeDecreaseRiskBufferFundPosition(
        uint128 _index,
        address payable _executionFeeReceiver
    ) external override nonReentrant returns (bool) {
        DecreaseRiskBufferFundPositionRequest memory request = decreaseRiskBufferFundPositionRequests[_index];
        if (request.account == address(0)) return true;

        bool shouldExecute = _shouldExecute(request.blockNumber, request.blockTime, request.account);
        if (!shouldExecute) return false;

        router.pluginDecreaseRiskBufferFundPosition{gas: executionGasLimit}(
            request.pool,
            request.account,
            request.liquidityDelta,
            request.receiver
        );

        _transferOutETH(request.executionFee, _executionFeeReceiver);

        delete decreaseRiskBufferFundPositionRequests[_index];

        emit DecreaseRiskBufferFundPositionExecuted(_index, _executionFeeReceiver);
        return true;
    }

    /// @inheritdoc IPositionRouter
    function executeDecreaseRiskBufferFundPositions(
        uint128 _endIndex,
        address payable _executionFeeReceiver
    ) external override onlyPositionExecutor {
        uint128 index = decreaseRiskBufferFundPositionIndex;
        _endIndex = uint128(Math.min(_endIndex, decreaseRiskBufferFundPositionIndexNext));

        while (index < _endIndex) {
            try this.executeDecreaseRiskBufferFundPosition(index, _executionFeeReceiver) returns (bool _executed) {
                if (!_executed) break;
            } catch {
                try this.cancelDecreaseRiskBufferFundPosition(index, _executionFeeReceiver) returns (bool _cancelled) {
                    if (!_cancelled) break;
                } catch {}
            }
            // prettier-ignore
            unchecked { ++index; }
        }

        decreaseRiskBufferFundPositionIndex = index;
    }

    /// @inheritdoc IPositionRouter
    function createIncreasePosition(
        IPool _pool,
        Side _side,
        uint128 _marginDelta,
        uint128 _sizeDelta,
        uint160 _acceptableTradePriceX96
    ) external payable override nonReentrant returns (uint128 index) {
        _side.requireValid();
        if (msg.value < minExecutionFee) revert InsufficientExecutionFee(msg.value, minExecutionFee);

        if (_marginDelta > 0) router.pluginTransfer(usd, msg.sender, address(this), _marginDelta);

        index = increasePositionIndexNext++;
        increasePositionRequests[index] = IncreasePositionRequest({
            account: msg.sender,
            pool: _pool,
            side: _side,
            marginDelta: _marginDelta,
            sizeDelta: _sizeDelta,
            acceptableTradePriceX96: _acceptableTradePriceX96,
            executionFee: msg.value,
            blockNumber: block.number.toUint96(),
            blockTime: block.timestamp.toUint64()
        });

        emit IncreasePositionCreated(
            msg.sender,
            _pool,
            _side,
            _marginDelta,
            _sizeDelta,
            _acceptableTradePriceX96,
            msg.value,
            index
        );
    }

    /// @inheritdoc IPositionRouter
    function cancelIncreasePosition(
        uint128 _index,
        address payable _executionFeeReceiver
    ) external override nonReentrant returns (bool) {
        IncreasePositionRequest memory request = increasePositionRequests[_index];
        if (request.account == address(0)) return true;

        bool shouldCancel = _shouldCancel(request.blockNumber, request.blockTime, request.account);
        if (!shouldCancel) return false;

        if (request.marginDelta > 0) usd.safeTransfer(request.account, request.marginDelta);

        _transferOutETH(request.executionFee, _executionFeeReceiver);

        delete increasePositionRequests[_index];

        emit IncreasePositionCancelled(_index, _executionFeeReceiver);

        return true;
    }

    /// @inheritdoc IPositionRouter
    function executeIncreasePosition(
        uint128 _index,
        address payable _executionFeeReceiver
    ) external override nonReentrant returns (bool) {
        IncreasePositionRequest memory request = increasePositionRequests[_index];
        if (request.account == address(0)) return true;

        bool shouldExecute = _shouldExecute(request.blockNumber, request.blockTime, request.account);
        if (!shouldExecute) return false;

        if (request.marginDelta > 0) usd.safeTransfer(address(request.pool), request.marginDelta);

        uint160 tradePriceX96 = router.pluginIncreasePosition{gas: executionGasLimit}(
            request.pool,
            request.account,
            request.side,
            request.marginDelta,
            request.sizeDelta
        );

        if (request.acceptableTradePriceX96 != 0)
            _validateTradePriceX96(request.side, tradePriceX96, request.acceptableTradePriceX96);

        _transferOutETH(request.executionFee, _executionFeeReceiver);

        delete increasePositionRequests[_index];

        emit IncreasePositionExecuted(_index, _executionFeeReceiver);
        return true;
    }

    /// @inheritdoc IPositionRouter
    function executeIncreasePositions(
        uint128 _endIndex,
        address payable _executionFeeReceiver
    ) external override onlyPositionExecutor {
        uint128 index = increasePositionIndex;
        _endIndex = uint128(Math.min(_endIndex, increasePositionIndexNext));

        while (index < _endIndex) {
            try this.executeIncreasePosition(index, _executionFeeReceiver) returns (bool _executed) {
                if (!_executed) break;
            } catch {
                try this.cancelIncreasePosition(index, _executionFeeReceiver) returns (bool _cancelled) {
                    if (!_cancelled) break;
                } catch {}
            }
            // prettier-ignore
            unchecked { ++index; }
        }

        increasePositionIndex = index;
    }

    /// @inheritdoc IPositionRouter
    function createDecreasePosition(
        IPool _pool,
        Side _side,
        uint128 _marginDelta,
        uint128 _sizeDelta,
        uint160 _acceptableTradePriceX96,
        address _receiver
    ) external payable override nonReentrant returns (uint128 index) {
        _side.requireValid();
        if (msg.value < minExecutionFee) revert InsufficientExecutionFee(msg.value, minExecutionFee);

        index = decreasePositionIndexNext++;
        decreasePositionRequests[index] = DecreasePositionRequest({
            account: msg.sender,
            pool: _pool,
            side: _side,
            marginDelta: _marginDelta,
            sizeDelta: _sizeDelta,
            acceptableTradePriceX96: _acceptableTradePriceX96,
            receiver: _receiver,
            executionFee: msg.value,
            blockNumber: block.number.toUint96(),
            blockTime: block.timestamp.toUint64()
        });

        emit DecreasePositionCreated(
            msg.sender,
            _pool,
            _side,
            _marginDelta,
            _sizeDelta,
            _acceptableTradePriceX96,
            _receiver,
            msg.value,
            index
        );
    }

    /// @inheritdoc IPositionRouter
    function cancelDecreasePosition(
        uint128 _index,
        address payable _executionFeeReceiver
    ) external override nonReentrant returns (bool) {
        DecreasePositionRequest memory request = decreasePositionRequests[_index];
        if (request.account == address(0)) return true;

        bool shouldCancel = _shouldCancel(request.blockNumber, request.blockTime, request.account);
        if (!shouldCancel) return false;

        _transferOutETH(request.executionFee, _executionFeeReceiver);

        delete decreasePositionRequests[_index];

        emit DecreasePositionCancelled(_index, _executionFeeReceiver);

        return true;
    }

    /// @inheritdoc IPositionRouter
    function executeDecreasePosition(
        uint128 _index,
        address payable _executionFeeReceiver
    ) external override nonReentrant returns (bool) {
        DecreasePositionRequest memory request = decreasePositionRequests[_index];
        if (request.account == address(0)) return true;

        bool shouldExecute = _shouldExecute(request.blockNumber, request.blockTime, request.account);
        if (!shouldExecute) return false;

        uint160 tradePriceX96 = router.pluginDecreasePosition{gas: executionGasLimit}(
            request.pool,
            request.account,
            request.side,
            request.marginDelta,
            request.sizeDelta,
            request.receiver
        );

        if (request.acceptableTradePriceX96 != 0)
            _validateTradePriceX96(request.side.flip(), tradePriceX96, request.acceptableTradePriceX96);

        _transferOutETH(request.executionFee, _executionFeeReceiver);

        delete decreasePositionRequests[_index];

        emit DecreasePositionExecuted(_index, _executionFeeReceiver);
        return true;
    }

    /// @inheritdoc IPositionRouter
    function executeDecreasePositions(
        uint128 _endIndex,
        address payable _executionFeeReceiver
    ) external override onlyPositionExecutor {
        uint128 index = decreasePositionIndex;
        _endIndex = uint128(Math.min(_endIndex, decreasePositionIndexNext));

        while (index < _endIndex) {
            try this.executeDecreasePosition(index, _executionFeeReceiver) returns (bool _executed) {
                if (!_executed) break;
            } catch {
                try this.cancelDecreasePosition(index, _executionFeeReceiver) returns (bool _cancelled) {
                    if (!_cancelled) break;
                } catch {}
            }
            // prettier-ignore
            unchecked { ++index; }
        }

        decreasePositionIndex = index;
    }

    // validation
    function _shouldCancel(
        uint256 _positionBlockNumber,
        uint256 _positionBlockTime,
        address _account
    ) internal view returns (bool) {
        return _shouldExecuteOrCancel(_positionBlockNumber, _positionBlockTime, _account);
    }

    function _shouldExecute(
        uint256 _positionBlockNumber,
        uint256 _positionBlockTime,
        address _account
    ) internal view returns (bool) {
        if (_positionBlockTime.add(maxTimeDelay) <= block.timestamp)
            revert Expired(_positionBlockTime.add(maxTimeDelay));

        return _shouldExecuteOrCancel(_positionBlockNumber, _positionBlockTime, _account);
    }

    function _shouldExecuteOrCancel(
        uint256 _positionBlockNumber,
        uint256 _positionBlockTime,
        address _account
    ) internal view returns (bool) {
        bool isExecutorCall = msg.sender == address(this) || positionExecutors[msg.sender];

        if (isExecutorCall) return _positionBlockNumber.add(minBlockDelayExecutor) <= block.number;

        if (msg.sender != _account) revert Forbidden();

        if (_positionBlockTime.add(minTimeDelayPublic) > block.timestamp)
            revert TooEarly(_positionBlockTime.add(minTimeDelayPublic));

        return true;
    }

    function _validateTradePriceX96(
        Side _side,
        uint160 _tradePriceX96,
        uint160 _acceptableTradePriceX96
    ) internal pure {
        // long makes price up, short makes price down
        if (
            (_side.isLong() && (_tradePriceX96 > _acceptableTradePriceX96)) ||
            (_side.isShort() && (_tradePriceX96 < _acceptableTradePriceX96))
        ) revert InvalidTradePrice(_tradePriceX96, _acceptableTradePriceX96);
    }

    function _transferOutETH(uint256 _amountOut, address payable _receiver) private {
        _receiver.sendValue(_amountOut);
    }
}

contracts/plugins/Router.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity =0.8.21;

import "./PluginManager.sol";
import "../libraries/SafeERC20.sol";
import "../tokens/interfaces/IEFC.sol";
import "../farming/interfaces/IRewardFarm.sol";
import "../staking/interfaces/IFeeDistributor.sol";

contract Router is PluginManager {
    IEFC public immutable EFC;
    IRewardFarm public immutable rewardFarm;
    IFeeDistributor public immutable feeDistributor;

    /// @notice Caller is not a plugin or not approved
    error CallerUnauthorized();
    /// @notice Owner mismatch
    error OwnerMismatch(address owner, address expectedOwner);

    constructor(IEFC _EFC, IRewardFarm _rewardFarm, IFeeDistributor _feeDistributor) {
        (EFC, rewardFarm, feeDistributor) = (_EFC, _rewardFarm, _feeDistributor);
    }

    /// @notice Transfers `_amount` of `_token` from `_from` to `_to`
    /// @param _token The address of the ERC20 token
    /// @param _from The address to transfer the tokens from
    /// @param _to The address to transfer the tokens to
    /// @param _amount The amount of tokens to transfer
    function pluginTransfer(IERC20 _token, address _from, address _to, uint256 _amount) external {
        _onlyPluginApproved(_from);
        SafeERC20.safeTransferFrom(_token, _from, _to, _amount);
    }

    /// @notice Transfers an NFT token from `_from` to `_to`
    /// @param _token The address of the ERC721 token to transfer
    /// @param _from The address to transfer the NFT from
    /// @param _to The address to transfer the NFT to
    /// @param _tokenId The ID of the NFT token to transfer
    function pluginTransferNFT(IERC721 _token, address _from, address _to, uint256 _tokenId) external {
        _onlyPluginApproved(_from);
        _token.safeTransferFrom(_from, _to, _tokenId);
    }

    /// @notice Open a new liquidity position
    /// @param _pool The pool in which to open liquidity position
    /// @param _account The owner of the position
    /// @param _margin The margin of the position
    /// @param _liquidity The liquidity (value) of the position
    /// @return positionID The position ID
    function pluginOpenLiquidityPosition(
        IPool _pool,
        address _account,
        uint128 _margin,
        uint128 _liquidity
    ) external returns (uint96 positionID) {
        _onlyPluginApproved(_account);
        return _pool.openLiquidityPosition(_account, _margin, _liquidity);
    }

    /// @notice Close a liquidity position
    /// @param _pool The pool in which to close liquidity position
    /// @param _positionID The position ID
    /// @param _receiver The address to receive the margin at the time of closing
    function pluginCloseLiquidityPosition(IPool _pool, uint96 _positionID, address _receiver) external {
        _onlyPluginApproved(_pool.liquidityPositionAccount(_positionID));
        _pool.closeLiquidityPosition(_positionID, _receiver);
    }

    /// @notice Adjust the margin of a liquidity position
    /// @param _pool The pool in which to adjust liquidity position margin
    /// @param _positionID The position ID
    /// @param _marginDelta The change in margin, positive for increasing margin and negative for decreasing margin
    /// @param _receiver The address to receive the margin when the margin is decreased
    function pluginAdjustLiquidityPositionMargin(
        IPool _pool,
        uint96 _positionID,
        int128 _marginDelta,
        address _receiver
    ) external {
        _onlyPluginApproved(_pool.liquidityPositionAccount(_positionID));
        _pool.adjustLiquidityPositionMargin(_positionID, _marginDelta, _receiver);
    }

    /// @notice Increase the liquidity of a risk buffer fund position
    /// @param _pool The pool in which to increase liquidity
    /// @param _account The owner of the position
    /// @param _liquidityDelta The increase in liquidity
    function pluginIncreaseRiskBufferFundPosition(IPool _pool, address _account, uint128 _liquidityDelta) external {
        _onlyPluginApproved(_account);
        _pool.increaseRiskBufferFundPosition(_account, _liquidityDelta);
    }

    /// @notice Decrease the liquidity of a risk buffer fund position
    /// @param _pool The pool in which to decrease liquidity
    /// @param _account The owner of the position
    /// @param _liquidityDelta The decrease in liquidity
    /// @param _receiver The address to receive the liquidity when it is decreased
    function pluginDecreaseRiskBufferFundPosition(
        IPool _pool,
        address _account,
        uint128 _liquidityDelta,
        address _receiver
    ) external {
        _onlyPluginApproved(_account);
        _pool.decreaseRiskBufferFundPosition(_account, _liquidityDelta, _receiver);
    }

    /// @notice Increase the margin/liquidity (value) of a position
    /// @param _pool The pool in which to increase position
    /// @param _account The owner of the position
    /// @param _side The side of the position (Long or Short)
    /// @param _marginDelta The increase in margin, which can be 0
    /// @param _sizeDelta The increase in size, which can be 0
    /// @return tradePriceX96 The trade price at which the position is adjusted.
    /// If only adding margin, it returns 0, as a Q64.96
    function pluginIncreasePosition(
        IPool _pool,
        address _account,
        Side _side,
        uint128 _marginDelta,
        uint128 _sizeDelta
    ) external returns (uint160 tradePriceX96) {
        _onlyPluginApproved(_account);
        return _pool.increasePosition(_account, _side, _marginDelta, _sizeDelta);
    }

    /// @notice Decrease the margin/liquidity (value) of a position
    /// @param _pool The pool in which to decrease position
    /// @param _account The owner of the position
    /// @param _side The side of the position (Long or Short)
    /// @param _marginDelta The decrease in margin, which can be 0
    /// @param _sizeDelta The decrease in size, which can be 0
    /// @param _receiver The address to receive the margin
    /// @return tradePriceX96 The trade price at which the position is adjusted.
    /// If only reducing margin, it returns 0, as a Q64.96
    function pluginDecreasePosition(
        IPool _pool,
        address _account,
        Side _side,
        uint128 _marginDelta,
        uint128 _sizeDelta,
        address _receiver
    ) external returns (uint160 tradePriceX96) {
        _onlyPluginApproved(_account);
        return _pool.decreasePosition(_account, _side, _marginDelta, _sizeDelta, _receiver);
    }

    /// @notice Close a position by the liquidator
    /// @param _pool The pool in which to close position
    /// @param _account The owner of the position
    /// @param _side The side of the position (Long or Short)
    /// @param _sizeDelta The decrease in size
    /// @param _receiver The address to receive the margin
    function pluginClosePositionByLiquidator(
        IPool _pool,
        address _account,
        Side _side,
        uint128 _sizeDelta,
        address _receiver
    ) external {
        _onlyLiquidator();
        _pool.decreasePosition(_account, _side, 0, _sizeDelta, _receiver);
    }

    /// @notice Collect the referral fee
    /// @param _pool The pool in which to collect referral fee
    /// @param _referralToken The id of the referral token
    /// @param _receiver The address to receive the referral fee
    /// @return The amount of referral fee received
    function pluginCollectReferralFee(
        IPool _pool,
        uint256 _referralToken,
        address _receiver
    ) external returns (uint256) {
        _onlyPluginApproved(EFC.ownerOf(_referralToken));
        return _pool.collectReferralFee(_referralToken, _receiver);
    }

    /// @notice Collect the liquidity reward
    /// @param _pools The pools in which to collect farm liquidity reward
    /// @param _owner The address of the reward owner
    /// @param _receiver The address to receive the reward
    /// @return rewardDebt The amount of liquidity reward received
    function pluginCollectFarmLiquidityRewardBatch(
        IPool[] calldata _pools,
        address _owner,
        address _receiver
    ) external returns (uint256 rewardDebt) {
        _onlyPluginApproved(_owner);
        return rewardFarm.collectLiquidityRewardBatch(_pools, _owner, _receiver);
    }

    /// @notice Collect the risk buffer fund reward
    /// @param _pools The pools in which to collect farm risk buffer fund reward
    /// @param _owner The address of the reward owner
    /// @param _receiver The address to receive the reward
    /// @return rewardDebt The amount of risk buffer fund reward received
    function pluginCollectFarmRiskBufferFundRewardBatch(
        IPool[] calldata _pools,
        address _owner,
        address _receiver
    ) external returns (uint256 rewardDebt) {
        _onlyPluginApproved(_owner);
        return rewardFarm.collectRiskBufferFundRewardBatch(_pools, _owner, _receiver);
    }

    /// @notice Collect the farm referral reward
    /// @param _pools The pools in which to collect farm risk buffer fund reward
    /// @param _referralTokens The IDs of the referral tokens
    /// @param _receiver The address to receive the referral reward
    /// @return rewardDebt The amount of the referral reward received
    function pluginCollectFarmReferralRewardBatch(
        IPool[] calldata _pools,
        uint256[] calldata _referralTokens,
        address _receiver
    ) external returns (uint256 rewardDebt) {
        uint256 tokensLen = _referralTokens.length;
        require(tokensLen > 0);

        address owner = EFC.ownerOf(_referralTokens[0]);
        _onlyPluginApproved(owner);
        for (uint256 i = 1; i < tokensLen; ++i)
            if (EFC.ownerOf(_referralTokens[i]) != owner) revert OwnerMismatch(EFC.ownerOf(_referralTokens[i]), owner);

        return rewardFarm.collectReferralRewardBatch(_pools, _referralTokens, _receiver);
    }

    /// @notice Collect EQU staking reward tokens
    /// @param _owner The staker
    /// @param _receiver The address used to receive staking reward tokens
    /// @param _ids Index of EQU tokens staking information that need to be collected
    /// @return rewardDebt The amount of staking reward tokens received
    function pluginCollectStakingRewardBatch(
        address _owner,
        address _receiver,
        uint256[] calldata _ids
    ) external returns (uint256 rewardDebt) {
        _onlyPluginApproved(_owner);
        return feeDistributor.collectBatchByRouter(_owner, _receiver, _ids);
    }

    /// @notice Collect Uniswap V3 positions NFT staking reward tokens
    /// @param _owner The Staker
    /// @param _receiver The address used to receive staking reward tokens
    /// @param _ids Index of Uniswap V3 positions NFTs staking information that need to be collected
    /// @return rewardDebt The amount of staking reward tokens received
    function pluginCollectV3PosStakingRewardBatch(
        address _owner,
        address _receiver,
        uint256[] calldata _ids
    ) external returns (uint256 rewardDebt) {
        _onlyPluginApproved(_owner);
        return feeDistributor.collectV3PosBatchByRouter(_owner, _receiver, _ids);
    }

    /// @notice Collect the architect reward
    /// @param _receiver The address used to receive rewards
    /// @param _tokenIDs The IDs of the Architect-type NFT
    /// @return rewardDebt The amount of architect rewards received
    function pluginCollectArchitectRewardBatch(
        address _receiver,
        uint256[] calldata _tokenIDs
    ) external returns (uint256 rewardDebt) {
        uint256 idsLen = _tokenIDs.length;
        require(idsLen > 0);

        address owner = EFC.ownerOf(_tokenIDs[0]);
        _onlyPluginApproved(owner);
        for (uint256 i = 1; i < idsLen; ++i)
            if (EFC.ownerOf(_tokenIDs[i]) != owner) revert OwnerMismatch(EFC.ownerOf(_tokenIDs[i]), owner);

        return feeDistributor.collectArchitectBatchByRouter(_receiver, _tokenIDs);
    }

    function _onlyPluginApproved(address _account) internal view {
        if (!isPluginApproved(_account, msg.sender)) revert CallerUnauthorized();
    }

    function _onlyLiquidator() internal view {
        if (!isRegisteredLiquidator(msg.sender)) revert CallerUnauthorized();
    }
}

contracts/staking/interfaces/IFeeDistrib.content:
// SPDX-License-Identifier: GPL-2.0-or-later
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

interface IFeeDistributor {
    /// @notice Emitted when EQU tokens are staked
    /// @param sender The address to apply for staking
    /// @param account Which address to stake to
    /// @param id Index of EQU tokens staking information
    /// @param amount The amount of EQU tokens that already staked
    /// @param period Lockup period
    event Staked(address indexed sender, address indexed account, uint256 indexed id, uint256 amount, uint16 period);

    /// @notice Emitted when Uniswap V3 positions NFTs are staked
    /// @param sender The address to apply for staking
    /// @param account Which address to stake to
    /// @param id Index of Uniswap V3 positions NFTs staking information
    /// @param amount The amount of Uniswap V3 positions NFT converted into EQU tokens that already staked
    /// @param period Lockup period
    event V3PosStaked(
        address indexed sender,
        address indexed account,
        uint256 indexed id,
        uint256 amount,
        uint16 period
    );

    /// @notice Emitted when EQU tokens are unstaked
    /// @param owner The address to apply for unstaking
    /// @param receiver The address used to receive the stake tokens
    /// @param id Index of EQU tokens staking information
    /// @param amount0 The amount of EQU tokens that already unstaked
    /// @param amount1 The amount of staking rewards received
    event Unstaked(
        address indexed owner,
        address indexed receiver,
        uint256 indexed id,
        uint256 amount0,
        uint256 amount1
    );

    /// @notice Emitted when Uniswap V3 positions NFTs are unstaked
    /// @param owner The address to apply for unstaking
    /// @param receiver The address used to receive the Uniswap V3 positions NFT
    /// @param id Index of Uniswap V3 positions NFTs staking information
    /// @param amount0 The amount of Uniswap V3 positions NFT converted into EQU tokens that already unstaked
    /// @param amount1 The amount of staking rewards received
    event V3PosUnstaked(
        address indexed owner,
        address indexed receiver,
        uint256 indexed id,
        uint256 amount0,
        uint256 amount1
    );

    /// @notice Emitted when claiming stake rewards
    /// @param owner The address to apply for claiming staking rewards
    /// @param receiver The address used to receive staking rewards
    /// @param id Index of EQU tokens staking information
    /// @param amount The amount of staking rewards received
    event Collected(address indexed owner, address indexed receiver, uint256 indexed id, uint256 amount);

    /// @notice Emitted when claiming stake rewards
    /// @param owner The address to apply for claiming staking rewards
    /// @param receiver The address used to receive staking rewards
    /// @param id Index of Uniswap V3 positions NFTs staking information
    /// @param amount The amount of staking rewards received
    event V3PosCollected(address indexed owner, address indexed receiver, uint256 indexed id, uint256 amount);

    /// @notice Emitted when claiming Architect-type NFT rewards
    /// @param receiver The address used to receive rewards
    /// @param tokenID The ID of the Architect-type NFT
    /// @param amount The amount of rewards received
    event ArchitectCollected(address indexed receiver, uint256 indexed tokenID, uint256 amount);

    /// @notice Emitted when deposit staking reward tokens
    /// @param amount The amount of staking reward tokens deposited
    /// @param equFeeAmount The amount of reward tokens allocated to the EQU
    /// @param architectFeeAmount The amount of reward tokens allocated to the Architect-type NFT pool
    /// @param perShareGrowthAfterX64 The adjusted `perShareGrowthX64`, as a Q96.64
    /// @param architectPerShareGrowthAfterX64 The adjusted `architectPerShareGrowthX64`, as a Q96.64
    event FeeDeposited(
        uint256 amount,
        uint256 equFeeAmount,
        uint256 architectFeeAmount,
        uint160 perShareGrowthAfterX64,
        uint160 architectPerShareGrowthAfterX64
    );

    /// @notice Emitted when the lockup periods and lockup multipliers are set
    /// @param lockupRewardMultiplierParameters The list of LockupRewardMultiplierParameter
    event LockupRewardMultipliersSet(LockupRewardMultiplierParameter[] lockupRewardMultiplierParameters);

    /// @notice The number of Architect-type NFT that has been mined is 0
    /// or the EQU tokens that Uniswap V3 positions NFTs converted into total staked amount is 0.
    error DepositConditionNotMet();
    /// @notice Invalid caller
    error InvalidCaller(address caller);
    /// @notice Invalid NFT owner
    error InvalidNFTOwner(address owner, uint256 tokenID);
    /// @notice Invalid lockup period
    error InvalidLockupPeriod(uint16 period);
    /// @notice Invalid StakeID
    error InvalidStakeID(uint256 id);
    /// @notice Not yet reached the unlocking time
    error NotYetReachedTheUnlockingTime(uint256 id);
    /// @notice Deposit amount is greater than the transfer amount
    error DepositAmountIsGreaterThanTheTransfer(uint256 depositAmount, uint256 balance);
    /// @notice The NFT is not part of the EQU-WETH pool
    error InvalidUniswapV3PositionNFT(address token0, address token1);
    /// @notice The exchangeable amount of EQU is 0
    error ExchangeableEQUAmountIsZero();
    /// @notice Invalid Uniswap V3 fee
    error InvalidUniswapV3Fee(uint24 fee);
    /// @notice The price range of the Uniswap V3 position is not full range
    error RequireFullRangePosition(int24 tickLower, int24 tickUpper, int24 tickSpacing);

    struct StakeInfo {
        uint256 amount;
        uint64 lockupStartTime;
        uint16 multiplier;
        uint16 period;
        uint160 perShareGrowthX64;
    }

    struct LockupRewardMultiplierParameter {
        uint16 period;
        uint16 multiplier;
    }

    /// @notice Get the fee token balance
    /// @return balance The balance of the fee token
    function feeBalance() external view returns (uint96 balance);

    /// @notice Get the fee token
    /// @return token The fee token
    function feeToken() external view returns (IERC20 token);

    /// @notice Get the total amount with multiplier of staked EQU tokens
    /// @return amount The total amount with multiplier of staked EQU tokens
    function totalStakedWithMultiplier() external view returns (uint256 amount);

    /// @notice Get the accumulated staking rewards growth per share
    /// @return perShareGrowthX64 The accumulated staking rewards growth per share, as a Q96.64
    function perShareGrowthX64() external view returns (uint160 perShareGrowthX64);

    /// @notice Get EQU staking information
    /// @param account The staker of EQU tokens
    /// @param stakeID Index of EQU tokens staking information
    /// @return amount The amount of EQU tokens that already staked
    /// @return lockupStartTime Lockup start time
    /// @return multiplier Lockup reward multiplier
    /// @return period Lockup period
    /// @return perShareGrowthX64 The accumulated staking rewards growth per share, as a Q96.64
    function stakeInfos(
        address account,
        uint256 stakeID
    )
        external
        view
        returns (uint256 amount, uint64 lockupStartTime, uint16 multiplier, uint16 period, uint160 perShareGrowthX64);

    /// @notice Get Uniswap V3 positions NFTs staking information
    /// @param account The staker of Uniswap V3 positions NFTs
    /// @param stakeID Index of Uniswap V3 positions NFTs staking information
    /// @return amount The amount of EQU tokens that Uniswap V3 positions NFTs converted into that already staked
    /// @return lockupStartTime Lockup start time
    /// @return multiplier Lockup reward multiplier
    /// @return period Lockup period
    /// @return perShareGrowthX64 The accumulated staking rewards growth per share, as a Q96.64
    function v3PosStakeInfos(
        address account,
        uint256 stakeID
    )
        external
        view
        returns (uint256 amount, uint64 lockupStartTime, uint16 multiplier, uint16 period, uint160 perShareGrowthX64);

    /// @notice Get withdrawal time period
    /// @return period Withdrawal time period
    function withdrawalPeriod() external view returns (uint16 period);

    /// @notice Get lockup multiplier based on lockup period
    /// @param period Lockup period
    /// @return multiplier Lockup multiplier
    function lockupRewardMultipliers(uint16 period) external view returns (uint16 multiplier);

    /// @notice The number of Architect-type NFTs minted
    /// @return quantity The number of Architect-type NFTs minted
    function mintedArchitects() external view returns (uint16 quantity);

    /// @notice Get the accumulated reward growth for each Architect-type NFT
    /// @return perShareGrowthX64 The accumulated reward growth for each Architect-type NFT, as a Q96.64
    function architectPerShareGrowthX64() external view returns (uint160 perShareGrowthX64);

    /// @notice Get the accumulated reward growth for each Architect-type NFT
    /// @param tokenID The ID of the Architect-type NFT
    /// @return perShareGrowthX64 The accumulated reward growth for each Architect-type NFT, as a Q96.64
    function architectPerShareGrowthX64s(uint256 tokenID) external view returns (uint160 perShareGrowthX64);

    /// @notice Set lockup reward multiplier
    /// @param lockupRewardMultiplierParameters The list of LockupRewardMultiplierParameter
    function setLockupRewardMultipliers(
        LockupRewardMultiplierParameter[] calldata lockupRewardMultiplierParameters
    ) external;

    /// @notice Deposite staking reward tokens
    /// @param amount The amount of reward tokens deposited
    function depositFee(uint256 amount) external;

    /// @notice Stake EQU
    /// @param amount The amount of EQU tokens that need to be staked
    /// @param account Which address to stake to
    /// @param period Lockup period
    function stake(uint256 amount, address account, uint16 period) external;

    /// @notice Stake Uniswap V3 positions NFT
    /// @param id The ID of the Uniswap V3 positions NFT
    /// @param account Which address to stake to
    /// @param period Lockup period
    function stakeV3Pos(uint256 id, address account, uint16 period) external;

    /// @notice Unstake EQU
    /// @param ids Indexs of EQU tokens staking information that need to be unstaked
    /// @param receiver The address used to receive the staked tokens
    /// @return rewardAmount The amount of staking reward tokens received
    function unstake(uint256[] calldata ids, address receiver) external returns (uint256 rewardAmount);

    /// @notice Unstake Uniswap V3 positions NFT
    /// @param ids Indexs of Uniswap V3 positions NFTs staking information that need to be unstaked
    /// @param receiver The address used to receive the Uniswap V3 positions NFTs
    /// @return rewardAmount The amount of staking reward tokens received
    function unstakeV3Pos(uint256[] calldata ids, address receiver) external returns (uint256 rewardAmount);

    /// @notice Collect EQU staking rewards through router
    /// @param owner The Staker
    /// @param receiver The address used to receive staking rewards
    /// @param ids Index of EQU tokens staking information that need to be collected
    /// @return rewardAmount The amount of staking reward tokens received
    function collectBatchByRouter(
        address owner,
        address receiver,
        uint256[] calldata ids
    ) external returns (uint256 rewardAmount);

    /// @notice Collect Uniswap V3 positions NFT staking rewards through router
    /// @param owner The Staker
    /// @param receiver The address used to receive staking reward tokens
    /// @param ids Index of Uniswap V3 positions NFTs staking information that need to be collected
    /// @return rewardAmount The amount of staking reward tokens received
    function collectV3PosBatchByRouter(
        address owner,
        address receiver,
        uint256[] calldata ids
    ) external returns (uint256 rewardAmount);

    /// @notice Collect rewards for architect-type NFTs through router
    /// @param receiver The address used to receive staking reward tokens
    /// @param tokenIDs The IDs of the Architect-type NFT
    /// @return rewardAmount The amount of staking reward tokens received
    function collectArchitectBatchByRouter(
        address receiver,
        uint256[] calldata tokenIDs
    ) external returns (uint256 rewardAmount);

    /// @notice Collect EQU staking rewards
    /// @param receiver The address used to receive staking reward tokens
    /// @param ids Index of EQU tokens staking information that need to be collected
    /// @return rewardAmount The amount of staking reward tokens received
    function collectBatch(address receiver, uint256[] calldata ids) external returns (uint256 rewardAmount);

    /// @notice Collect Uniswap V3 positions NFT staking rewards
    /// @param receiver The address used to receive staking reward tokens
    /// @param ids Index of Uniswap V3 positions NFTs staking information that need to be collected
    /// @return rewardAmount The amount of staking reward tokens received
    function collectV3PosBatch(address receiver, uint256[] calldata ids) external returns (uint256 rewardAmount);

    /// @notice Collect rewards for architect-type NFTs
    /// @param receiver The address used to receive rewards
    /// @param tokenIDs The IDs of the Architect-type NFT
    /// @return rewardAmount The amount of staking reward tokens received
    function collectArchitectBatch(
        address receiver,
        uint256[] calldata tokenIDs
    ) external returns (uint256 rewardAmount);
}

